mapper.insert:
select decode(#{file_sno}, 0, seq_ck_atch_file.nextval, #{file_sno}) from dual
insert into ck_atch_file ( file_sno, file_no, kpn_url, file_dsp_nm, file_kpn_nm, file_estn_nm, file_sz_byte, reg_usr_no, mod_usr_no, reg_date, mod_date
) values ( #{file_sno}, ( select nvl(max(file_no), 0) + 1 from ck_atch_file where file_sno = #{file_sno}
), #{kpn_url}, #{file_dsp_nm}, #{file_kpn_nm}, #{file_estn_nm}, #{file_sz_byte}, #{reg_usr_no}, #{reg_usr_no}, sysdate, sysdate )
